// Javascript
const arr = ["Muhammad", "Azril", "Fahrezi"];

function greeting() {
  const name = arr.find((item) => item === "Muhammad");
  return `Hello, ${name}!`;
}

console.log(greeting()); // Hello, Muhammad!

// TypeScript
const arr: string[] = ["Muhammad", "Azril", "Fahrezi"];

function greeting(): string {
  const name: string | undefined = arr.find((item) => item === "Muhammad");
  return `Hello, ${name}!`;
}

console.log(greeting()); // Hello, Muhammad!
